/**
 * Shuffles array in place.
 * @param a items An array containing the items.
 */
export function array_shuffle(a: any): any;
/**
 * get all keys
 * @param haystack string etc
 */
export function array_keys(haystack: any): string[];
/**
 * in_array PHP equivalent
 * @param needle string etc
 * @param haystack
 */
export function in_array(needle: any, haystack: any): boolean;
/**
 * Deep merge two or more objects into the first.
 * (c) 2021 Chris Ferdinandi, MIT License, https://gomakethings.com
 * @param objects  The objects to merge together
 * @returns Merged values of defaults and options
 */
export function deepAssign(...args: any[]): any;
/**
 * Remove item from array
 * @param arr
 * @param value
 * @returns
 */
export function removeItem(arr: any, value: any): any;
